mapper.insert:
INSERT INTO user
SET user_id = #{userId}
, upw = #{upw}
, nick_name = #{nickName}
, pic = #{pic}

mapper.select:
SELECT user_id as userId
, upw
FROM user
WHERE user_id = #{userId}
AND upw = #{upw}

SELECT user_id as userId, nick_name as nickName, pic
FROM user
WHERE user_id = #{userId}

